    private void btncalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncalcularActionPerformed
        // Bot√£o calcular
        //1 passo - criar um objeto da classe Soma
        
        Soma obj = new Soma();
        //obj.v1 = Double.parseDouble(txtv1.getText());   //metodo indireto
        //obj.v2 = Double.parseDouble(txtv2.getText());   //metodo indireto
        obj.setV1 (Double.parseDouble(txtv1.getText()));
        obj.setV2 (Double.parseDouble(txtv2.getText()));
        
        obj.calcularSoma();
        
        //txttotal.setText(String.valueOf(obj.total)); //metodo direto
        txttotal.setText(String.valueOf(obj.getTotal()));  //metodo indireto
        
    }//GEN-LAST:event_btncalcularActionPerformed

